mapper.select:

select mmgrade,mmgrade,scmmid,mmname,mmgender,eschool,eschoolgy,hschool,hschoolgy,mschool,mschoolgy,uschool,uschooley 
from scname


	
		and mmname like '%' || #{mmname} ||'%' and eschool like '%' || #{eschool} ||'%' and mmgrade ='N';
			
	
		and eschool like '%' || #{eschool} ||'%' and mmgrade ='N';
	
	
		and eschoolgy like '%' || #{eschoolgy} ||'%' and mmgrade ='N';


select mmgrade,scmmid,mmname,mmgender,eschool,eschoolgy,hschool,hschoolgy,mschool,mschoolgy,uschool,uschooley 
from scname
							
	
		and mmname like '%' || #{mmname} || '%' and mschool like '%' || #{mschool} || '%' and mmgrade ='N';
		
	
		and mschool like '%' || #{mschool} || '%' and mmgrade ='N';
	
	
		and mschoolgy like '%' || #{mschoolgy} ||'%' and mmgrade ='N';


select mmgrade,scmmid,mmname,mmgender,eschool,eschoolgy,hschool,hschoolgy,mschool,mschoolgy,uschool,uschooley
from scname
							
	
		and mmname like '%' || #{mmname} || '%' and hschool like '%' || #{hschool} || '%' and mmgrade ='N';
		
	
		and hschool like '%' || #{hschool} || '%' and mmgrade ='N';
	
	
		and hschoolgy like '%' || #{hschoolgy} ||'%' and mmgrade ='N';


select mmgrade,scmmid,mmname,mmgender,eschool,eschoolgy,hschool,hschoolgy,mschool,mschoolgy,uschool,uschooley,uschoolma
from scname
							
	
		and mmname like '%' || #{mmname} || '%' and uschool like '%' || #{uschool} || '%' and mmgrade ='N';
		
	
		and uschool like '%' || #{uschool} || '%' and mmgrade ='N';
	
	
		and uschooley like '%' || #{uschooley} ||'%' and mmgrade ='N';
	
	
		and uschoolma like '%' || #{uschoolma} ||'%' and mmgrade ='N';
